% This script create random number of CAG repeats on the ORF of the HTT reference gene

clear

% Choose the number of sequence to generate
interface=menu('Generate DNA sequence with random CAG repeats.', 'Single sequence', 'Multiple sequences', 'End');
while interface ~= 3
    if interface == 1
        % Choose the type of sequence to generate
        close
        seqNum = 1;
        seqType = menu('Choose sequence type', 'Positive', 'Negative', 'N/A');
        [singleSeq] = seqGen(seqNum,seqType);
        nameInput = input('Enter file''s name: ', 's');
        fileName = sprintf('%s.txt', nameInput);
        disp('A new sequence was generated.');
        saveSeq(singleSeq, fileName, interface)
%        str = '';
        pattern = 'ATG((?:[ACGT]{3})*?)(?:TAG|TAA|TGA)'; % match the start codon with the sequence 
        arrayCodingSeq = cellstr(regexp(singleSeq, pattern, 'match', 'once')); % a new sequence of subject sequence from start codon to stop codon        
        codon = strread(arrayCodingSeq{:},'%3s'); % Cut the genome sequence to codon - a group of 3 nucleotides
        string = 'CAG'; % Set the target codon
        codoncmp = strcmp(codon, string); % Compare if codons in sequence is "CAG", 1 is similar codon, 0 is different
        count = 0; % Initialize a counter variable
        lengths = []; % Initialize an array to store the lengths of the consecutive sequences
        for i = 1:length(codoncmp) % Loop through the array
            if codoncmp(i) == 1 % If the current element is 1
                count = count + 1; % Increment the counter
            else % If the current element is 0
                lengths = [lengths count]; % Add the count to the lengths array
                count = 0; % Reset the counter
            end
        end
        repeats = max(lengths);
        disp(repeats)
        
    elseif interface == 2
% Creating 10 positive sequences
%         preNum = 10;
%         seqType = 1;
%         [seqList] = seqGen(preNum,seqType);
%         fileName = char(arrayfun(@(x) sprintf('posSeq%d.txt', x), 1:preNum, 'UniformOutput',false));
%         saveSeq(seqList, fileName, interface)

        seqNum = input('How many random sequences do you want to generate?\n');
        seqType = 0;
        [seqList] = seqGen(seqNum,seqType);
        fileName = char(arrayfun(@(x) sprintf('randomSeq%d.txt', x), 1:seqNum, 'UniformOutput',false));
        saveSeq(seqList, fileName,interface)
        fprintf('%d new random sequences were generated.\n', seqNum);
    elseif interface == 3
        close
        disp('The program is terminated.')
    end
    
    interface=menu('Generate DNA sequence with random CAG repeats.', 'Single sequence', 'Multiple sequences', 'End');
    if interface == 3
        close
        disp('The program is terminated.')
    end

end

function [seqMemory] = seqGen(number, type)

%str = '';

if type == 1
    close
    seq = 'positive';
    numMutation = randi([40,130], 1, number);
    %numMutation = input('NUMBER'); %If want to create a specific length
elseif type == 2
    close
    seq = 'negative';
    numMutation = randi([0,35], 1, number);
elseif type == 3
    close
    seq = 'N/A';
    numMutation = randi([36,39], 1, number);
elseif type == 0
    close
    seq = 'random';
    numMutation = randi([0, 45], 1, number);
end

% Using raw sequence as template
rawSeq = '';
str = '';
codon = 'CAG';
start_end = regexp(rawSeq, str, 'split'); % Get the start and end of the sequences
start = start_end(1);

seqMemory = repmat(rawSeq, number, 1); % Preallocate storage for mutated sequences

% Generate an array of repeats
for i = 1:number
    currentMutation = numMutation(i)
    randomPoint = randi([2, 3143-currentMutation-1],1,1);
    randomPos = randomPoint*3-1+length(start); % Mutate the raw sequence
    currentSeq = seqMemory(i,:);
    mutatedSeq = repmat(codon,1,currentMutation);
    currentSeq(randomPos:randomPos+currentMutation*3-1) = mutatedSeq;
    if number == 1
        checkpreCAG = randomPos - 3; % Position of codon right before repeats
        checkpostCAG = randomPos + numMutation*3; % Position of codon right after repeats
        currentSeq(checkpreCAG:checkpreCAG+2) = 'AAA';
        currentSeq(checkpostCAG:checkpostCAG+2) = 'AAA';
    end
    seqMemory(i,:) = currentSeq;
end


seqMemory = cellstr(seqMemory);

end


function saveSeq(list, name, singMul)

elements = length(list(:,1));

disp('Please select the desired folder!')
txtpath = uigetdir('Save the sequence to:');

% Save new sequence to txt file
for i = 1:elements
    txtname =  name(i,:); % Append name to root
    fulltxt = fullfile(txtpath,txtname);
    writelines(list(i), fulltxt); % Write new sequence into txt file(s)
end

if singMul == 1
    fprintf('New sequence saved to %s.\n', fulltxt)
elseif singMul == 2 && elements > 40
    fprintf('%d new sequences saved to %s.\n', elements, fulltxt)
else

end

end